import { React, useState } from "react";

import axios from "axios";

function AgregarProducto() {
    // agregamos los hooks para poder utilizar el atributo value 
    const [name, setName] = useState('')
    const [img, setImg] = useState('')
    const [price, setPrece] = useState('')




function agregarProducto ()   {

        var nproducto = {
            name: name,
            img: img,
            price: price
           
        }

        console.log(nproducto)

        
        const { name, img, price } = nproducto;
        axios.post('http://localhost:4000/producto', { name, img, price })
        .then(res => {
            alert(res.data)
        })
        .then(err => { console.log(err) })

           
            
          




/*
        axios.post('http://localhost:4000/products', nproducto)
            .then(res => {
                alert(res.data)
            })
            .then(err => { console.log(err) })
            */
          

           

        ////****** */


    };



    return (
        <div className="container">

            <div className="row">

                <h2 className="mt-4">crear nuevo usuario</h2>
            </div>


            <div className="row"></div>

            <div className="col-sm-6 offset-3">

                <div >
                    <label htmlFor="nombre" >nombre</label>
                    <input type="text" value={name} onChange={(e) => { setName(e.target.value) }}></input>
                </div>

                <div >
                    <label htmlFor="email" >Imagen</label>
                    <input type="email" value={img} onChange={(e) => { setImg(e.target.value) }}></input>
                </div>

                <div>
                    <label htmlFor="telefono" >precio</label>
                    <input type="text" value={price} onChange={(e) => { setPrece(e.target.value) }}></input>
                </div>


                <button onClick={agregarProducto} >agregar producto</button>


            </div>

        </div>

    );
};

export default AgregarProducto
