import React, {  useState } from "react";
import axios from "axios";
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

const AgregarProducto = () => {
  /* Traemos del context la funcion para agregar un producto */
 

  const [name, setName] = useState('')
    const [img, setImg] = useState('')
    const [price, setPrece] = useState('')

    function agregarProducto() {

      var nproducto = {
          name: name,
          img: img,
          price: price,
          
      }
    
    
      console.log(nproducto)
    
      axios.post('http://localhost:4000/agregarproducto', {name,img,price})
      .then(res => {
          alert(res.data)
      })
      .then(err => { console.log(err) })

      
    
    }

  return (
    
    <div className="container">

        <div className="row">

            <h2 className="mt-4">crear nuevo producto</h2>
        </div>


        <div className="row"></div>

        <div class="container-fluid col-6 col-md-6 pt-3 ps-4  bg-success bg-opacity-85 text-white fst-italic d-flex justify-content-center">


        <Form.Group className="mb-5" controlId="formBasicUsuario">
                        <Form.Label>                <label htmlFor="nombre" >nombre</label>
</Form.Label>
                        <Form.Control type="user"

                            onChange={(e) => {
                                setDate({ ...data, user: e.target.value })
                            }
                            } placeholder="Ingrese un Nombre" />
                    </Form.Group>



            <div >
                <label htmlFor="nombre" >nombre</label>
                <input type="text" value={name} onChange={(e) => { setName(e.target.value) }}></input>
            </div>

            <div >
                <label htmlFor="img" >Imagen</label>
                <input type="text" value={img} onChange={(e) => { setImg(e.target.value) }}></input>
            </div>

            <div>
                <label htmlFor="price" >precio</label>
                <input type="text" value={price} onChange={(e) => { setPrece(e.target.value) }}></input>
            </div>


            <button onClick={agregarProducto} >Cear producto</button>
        </div>


    </div>


)

  
};

export default AgregarProducto
