import React, { useContext, useState } from "react";
import CartContext from "../../context/CartContext";



const AgregarProducto = () => {
  /* Traemos del context la funcion para agregar un producto */
  const { addItemToCart, products } = useContext(CartContext);

  const [name, setName] = useState('')
    const [img, setImg] = useState('')
    const [price, setPrece] = useState('')

    function agregarProducto() {

      var nproducto = {
          name: name,
          img: img,
          price: price,
          
      }
    
    
      console.log(nproducto)
    
      
    
    }

  return (
    <>
    <div className="container">

        <div className="row">

            <h2 className="mt-4">crear nuevo usuario</h2>
        </div>


        <div className="row"></div>

        <div className="col-sm-6 offset-3">

            <div >
                <label htmlFor="nombre" >nombre</label>
                <input type="text" value={name} onChange={(e) => { setName(e.target.value) }}></input>
            </div>

            <div >
                <label htmlFor="email" >Imagen</label>
                <input type="email" value={img} onChange={(e) => { setImg(e.target.value) }}></input>
            </div>

            <div>
                <label htmlFor="telefono" >precio</label>
                <input type="text" value={price} onChange={(e) => { setPrece(e.target.value) }}></input>
            </div>


            <button onClick={agregarProducto} > guardar usuario</button>
        </div>


    </div>


<div className={styles.productsContainer}>
      {products &&
        products.map((product, i) => (
          <div key={i} className={styles.product}>
            <img src={product.img} alt={product.name} />
            <div>
              <p>
                {product.name} - ${product.price}
              </p>
            </div>
            {!product.inCart ? (
              <button onClick={() => addItemToCart(product)}>
                Add to Cart
              </button>
            ) : (
              <button>En el carrito</button>
            )}
          </div>
        ))}
    </div>
</>
)

  
};

export default AgregarProducto
