import "./App.scss";
import React from "react";
import Home from "./components/Home";
import { CartProvider } from "./context/CartContext";


const App = () => {
  /* Envolvemos la home con el provider del context */


  return (
    <div className="App">


      <>
      
           
            <CartProvider>
              <Home />
            </CartProvider>
   
            </>

    </div >
  );
};

export default App;
