import React from "react";
import { Link } from "react-router-dom";
import {JsonData} from "../data/UsuariosEjemplo.json";




function Validacion(usuarioNombre, contrasena) {

  var decision = false;
  var datos = JsonData; //Integra o con express o con mongoDb   


  for (const usuario of datos) {
      if (usuarioNombre === usuario.rol && contrasena === usuario.pass) {
          decision = true;
      }
  }
  return decision;
}


export const MenuNavegacion = ({ rol }) => {



  return (

    

    <nav className="navbar navbar-expand-lg navbar-light bg-secondary border border-start-0 fw-bolder fs-5">
      
    


      <button
        className="navbar-toggler "
        type="button"
        data-toggle="collapse"
        data-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse " id="navbarNav">
      
        {rol === "Admin" ? (
          <ul className="navbar-nav">
            <li className="nav-item active">
              <Link className="nav-link  text-white" to={"/"}>Login</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white" to={"/ListaProductosAdmin"}>Lista productos</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white" to={"/CrearProductos"}>Crear Nuevos productos
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white " to={"/ListaVentas"}>Lista de ventas
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white " to={"/CrearUsuarios"}>Crear CrearUsuarios
              </Link>
            </li>

          

          </ul>
        ) : (
          <ul className="navbar-nav ">
            <li className="nav-item active">
              <Link className="nav-link" to={"/"}>Login</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to={"/ListaProductosUsuario"}>Productos</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to={"/CarritoCompras"}>CArrito de Compras
              </Link>
            </li>

          </ul>
        )}
      </div>
    </nav>
  );
};
